--
-- Table structure for table `as_posts`
--

DROP TABLE IF EXISTS `as_posts`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `as_posts` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_author` bigint(20) unsigned NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext NOT NULL,
  `post_title` text NOT NULL,
  `post_excerpt` text NOT NULL,
  `post_status` varchar(20) NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) NOT NULL DEFAULT 'open',
  `post_password` varchar(20) NOT NULL DEFAULT '',
  `post_name` varchar(200) NOT NULL DEFAULT '',
  `to_ping` text NOT NULL,
  `pinged` text NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `guid` varchar(255) NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ID`),
  KEY `post_name` (`post_name`),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),
  KEY `post_parent` (`post_parent`),
  KEY `post_author` (`post_author`)
) ENGINE=InnoDB AUTO_INCREMENT=85 DEFAULT CHARSET=utf8;
/*!40101 SET character_set_client = @saved_cs_client */;

--
-- Dumping data for table `as_posts`
--

LOCK TABLES `as_posts` WRITE;
/*!40000 ALTER TABLE `as_posts` DISABLE KEYS */;
INSERT INTO `as_posts` VALUES (1,1,'2015-04-03 20:51:52','2015-04-03 20:51:52','Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!','Hello world!','','trash','open','open','','hello-world','','','2015-04-03 20:59:48','2015-04-03 20:59:48','',0,'http://wpastronauten.me/?p=1',0,'post','',1),(2,1,'2015-04-03 20:51:52','2015-04-03 20:51:52','This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href=\"http://wpastronauten.me/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!','Sample Page','','trash','open','open','','sample-page','','','2015-04-03 20:59:42','2015-04-03 20:59:42','',0,'http://wpastronauten.me/?page_id=2',0,'page','',0),(4,1,'2015-04-03 20:59:42','2015-04-03 20:59:42','This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href=\"http://wpastronauten.me/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!','Sample Page','','inherit','open','open','','2-revision-v1','','','2015-04-03 20:59:42','2015-04-03 20:59:42','',2,'http://wpastronauten.me/2-revision-v1/',0,'revision','',0),(5,1,'2015-04-03 20:59:48','2015-04-03 20:59:48','Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!','Hello world!','','inherit','open','open','','1-revision-v1','','','2015-04-03 20:59:48','2015-04-03 20:59:48','',1,'http://wpastronauten.me/1-revision-v1/',0,'revision','',0),(6,1,'2015-04-03 21:07:51','2015-04-03 21:07:51','<p class=\"p1\"><span class=\"s1\">Als theatermakers en performers streven wij er naar het publiek een persoonlijke ervaring te geven. Zowel de toevallige voorbijganger als het doelgericht publiek gunnen wij de ontmoeting tussen onze en hun eigen verbeelding. Dus wij spelen; op straat, in een tent, op vaste tijden, maar het kan ook verspreid over een groot terrein en de hele dag door. Met groot enthousiasme hoog van de toren blazen of juist klein en poëtisch spelen we met ons publiek. Bovendien trekken wij met onze werkwijze buiten de gebaande paden van het theater door interdisciplinaire samenwerkingen aan te gaan met o.a. mode- en beeldend studenten.</span></p>\r\n\r\n<h1 class=\"p1\">Maikel</h1>','Wie zijn wij','','publish','open','open','','wie-zijn-wij','','','2015-04-08 10:26:18','2015-04-08 10:26:18','',0,'http://wpastronauten.me/?page_id=6',0,'page','',0),(7,1,'2015-04-03 21:07:51','2015-04-03 21:07:51','',