sp;<span>(<a href=\'plugin-install.php?tab=plugin-information&amp;plugin=nextgen-facebook&amp;_wpnonce=ae0fe5caae&amp;TB_iframe=true&amp;width=600&amp;height=800\' class=\'thickbox\' title=\'NextGEN Facebook - Advanced Social SEO for Facebook, Google+, Pinterest, Twitter &amp; More\'>Install</a>)</span></li></ul></div>','no'),(127,'theme_mods_twentyfifteen','a:1:{s:16:\"sidebars_widgets\";a:2:{s:4:\"time\";i:1422318513;s:4:\"data\";a:2:{s:19:\"wp_inactive_widgets\";a:0:{}s:9:\"sidebar-1\";a:6:{i:0;s:8:\"search-2\";i:1;s:14:\"recent-posts-2\";i:2;s:17:\"recent-comments-2\";i:3;s:10:\"archives-2\";i:4;s:12:\"categories-2\";i:5;s:6:\"meta-2\";}}}}','yes'),(128,'current_theme','Goosefield','yes'),(129,'theme_mods_goosefield','a:1:{i:0;b:0;}','yes'),(130,'theme_switched','','yes');
